Action.Str:
if (global('%TIME') >= 0 && global('%TIME') < 4 ) 
{
setGlobal( Greet,'Good LateNight');
}
else if (global('%TIME') >= 4 && global('%TIME') < 12)
{
setGlobal( Greet,'Good Morning');
}
else if (global('%TIME') >= 12 && global('%TIME') < 15)
{
setGlobal( Greet,'Good Noon');
}
else if (global('%TIME') >= 15 && global('%TIME') < 18)
{
setGlobal( Greet,'Good Afternoon');
}
else if (global('%TIME') >= 18 && global('%TIME') < 21)
{
setGlobal( Greet,'Good Evening');
}
else if (global('%TIME') >= 21 && global('%TIME') < 0)
{
setGlobal( Greet,'Good Night');
}

var a = local('weather_by_ip');
var b =JSON.parse(a);
setLocal( 'description',b.weather[0].description);
setLocal( 'temp',b.main.temp);
setLocal('humidity',b.main.humidity);
setLocal('temp_min',b.main.temp_min);
setLocal('temp_max',b.main.temp_max);
setLocal('wind_speed',b.wind.speed);
setLocal('wind_deg',b.wind.deg);
setLocal('clouds',b.clouds.all);
setLocal('name',b.name);

var a = local('loction_by_ip');
var b =JSON.parse(a);
setLocal( 'latobj',b.latitude );
setLocal( 'longobj',b.longitude);

